import { Component, Input, Output, EventEmitter } from '@angular/core';
import { Product, products } from '../products';

@Component({
  selector: 'app-product-item',
  templateUrl: './product-item.component.html',
  styleUrls: ['./product-item.component.css']
})
export class ProductItemComponent {
  @Input() item!: Product;
  @Output() remove = new EventEmitter();
  like: number = 0;
  share(url : string) {
    var otvet = window.prompt()
    var link = "https://t.me/share/url?url="+url+"&text=" + otvet;
    window.open(link);
  }

  onNotify() {
    window.alert('You will be notified when the product goes on sale');
  }

  deleteItem(){
    this.remove.emit(this.item.id);
  }
  likeUp(){
    this.like++;
  }
}


<div class = "list-block">

    <h3>
        <a [title] = "item.name + ' details'"
        [routerLink]="['/products', item.id]">>
          {{ item.name }}
        </a>

    </h3>

    <div class="picture">
      <img [src] = item.image>
    </div>

    <p *ngIf="item.description">
      Description: {{ item.description }}
    </p>

    <p *ngIf="item.rating">
      rating: {{ item.rating }}
    </p>

    <button type="button" (click)="share(item.link)">
      Share
    </button>
    <button type="button" (click)="deleteItem()">
        Delete
    </button>
    <button type="button" (click)="likeUp(item.id)" class = "like-button">
      <i class="fa-solid fa-heart fa-xl" ></i>
      {{product.like}}
    </button>

    <app-product-alerts
  [product]="item" 
  (notify)="onNotify()">
</app-product-alerts>

</div>

